<template>
  <f7-page data-page="employee">
    <f7-navbar >
      <f7-nav-left back-link="上一頁" sliding></f7-nav-left>
      <f7-nav-center>照顧員</f7-nav-center>
    </f7-navbar>
    <div v-for="today in vehiclesCheckIns">
      <f7-block-title>{{renderDate (today.date)}}</f7-block-title>
      <f7-list media-list>
        <f7-list-item  v-for="vehicle in today.vehicles"
          :media = "renderMedia (vehicle.name)"
          :title = "vehicle.name"
          :badge = "'員工號: ' + vehicle.model"
          badge-color = "pink" 
        >
        </f7-list-item>
      </f7-list>
    </div>
  </f7-page>
</template>


<script>
import moment from 'moment';

export default {
  data: function (){
    return {
      vehicleClassMap : {
        '餐飲科' : 'food',
        '健康課程' : 'health-class',
        '維護管理': 'maintainance',
        '健康醫療': 'medicine',
        '體適能': 'fitness'
      }
    }
  },
  computed: {
    vehiclesCheckIns () {
      return this.$store.state.vehicles;
    }
  },
  methods: {
    renderMedia (name) 
    {
      return '<i class="f7-icons ' + this.vehicleClassMap [name] + '\" />';
    },
    renderDate (epochTime) 
    {
      const date = new Date (epochTime);
      const time = moment (date).format ('LL');
      const day = moment(date).format('dddd'); 
      const fromNow = moment(date).fromNow (); 

      return time + " " + day + " ( " + fromNow + " )";
    }
  }
}
</script>
